let status = flow.get("status_summary");
if (status===undefined) {
    status = [];
}

if (msg.topic==="reset") {
    status = [];
    flow.set("status_summary", status);
    msg.payload = status;
    return msg;
}

let now = new Date();
let key = msg.status.fill + "|" + msg.status.shape + "|" + msg.status.source.name;

let found = false;
for (let i=0;i<status.length;i++) {
    if (status[i].key === key) {
        // if match is found, increase the counter and update the time
        status[i].count++;
        status[i].timestamp = now.getTime();
        status[i].time = now.toLocaleString();
        status[i].text = msg.status.text;
        found = true;
        break;
    }
}

// This error is not logged before, create it
if (!found) {
    status.push({ "key": key, "timestamp": now.getTime(), "fill": msg.status.fill, "shape": msg.status.shape, "text": msg.status.text, "source": msg.status.source.name, "type": msg.status.source.type, "count": 1, "time" : now.toLocaleString()});
}

flow.set("status_summary", status);

msg.payload = status;
return msg;

const collectionsize = 10; // define the number of error to be collected. Once limit is reached, older error is deleted.

let status = flow.get("status_all");
if (status===undefined) {
    status = [];
}

if (msg.topic==="reset") {
    status = [];
    flow.set("status_all", status);
    msg.payload = status;
    return msg;
}

let now = new Date();

if (status.length >= collectionsize) {
    status.shift();
}

status.push({ "timestamp": now.getTime(), "fill": msg.status.fill, "shape": msg.status.shape, "text": msg.status.text, "source": msg.status.source.name, "type": msg.status.source.type, "time": now.toLocaleString() });

flow.set("status_all", status);

msg.payload = status;
return msg;

